async function checkKeyValidity(key) {
  try {
    const { data } = await axios.get("https://raw.githubusercontent.com/GanzJbganteng/id/main/auth.json");
    return data.find(entry => entry.KEY === key && entry.STATUS === "active");
  } catch (e) {
    console.error("Gagal ambil database KEY:", e.message);
    return null;
  }
}

bot.command("addpairing", async (ctx) => {
  const userId = ctx.from.id;

  if (rofik && rofik.user) {
    return await ctx.reply(`‚úÖ WhatsApp sudah terhubung.\n\nNama: ${rofik.user.name || 'Tidak diketahui'}\nNomor: ${rofik.user.id.split(":")[0]}`);
  }

  pendingKeys.set(userId, { attempts: 3, step: "key" });
  return await ctx.reply("üîê Masukkan lisensi KEY Anda:");
});

bot.on("text", async (ctx) => {
  const userId = ctx.from.id;
  const state = pendingKeys.get(userId);
  const text = ctx.message.text?.trim();

  if (!state) return;

  // STEP 1: Validasi KEY
  if (state.step === "key") {
    const result = await checkKeyValidity(text);

    if (!result) {
      state.attempts--;
      if (state.attempts <= 0) {
        pendingKeys.delete(userId);
        return await ctx.reply("üö´ KEY salah. Akses ditolak.");
      }
      return await ctx.reply(`‚ùå KEY tidak valid. Sisa percobaan: ${state.attempts}`);
    }

    pendingKeys.set(userId, { ...state, step: "number" });
    return await ctx.reply("‚úÖ KEY valid!\n\nüìû Masukkan nomor WhatsApp (format: 62xxxx):");
  }

  // STEP 2: Proses nomor dan pairing
  if (state.step === "number") {
    let phoneNumber = text.replace(/[^0-9]/g, '');
    if (phoneNumber.length < 8) {
      return await ctx.reply("‚ùå Nomor telepon tidak valid. Minimal 8 digit.");
    }
    if (!phoneNumber.startsWith("62")) phoneNumber = "62" + phoneNumber;

    if (!rofik) {
      try {
        await startSesi(); // fungsi inisialisasi WhatsApp
        await new Promise(res => setTimeout(res, 3000));
      } catch (err) {
        console.error("Gagal inisialisasi WA:", err.message);
        return await ctx.reply("‚ùå Gagal menghubungkan WhatsApp.");
      }
    }

    try {
      const code = await rofik.requestPairingCode(phoneNumber);
      const formattedCode = code?.match(/.{1,4}/g)?.join("-") || code;

      pendingKeys.delete(userId);
      await ctx.replyWithPhoto("https://img1.pixhost.to/images/6514/611451276_kurosay.jpg", {
        caption: `‚ï≠‚îÄ‚îÄ„Äå ùó£ùóîùóúùó•ùóúùó°ùóö ùóñùó¢ùóóùóò „Äç\n‚îÉ Nomor: ${phoneNumber}\n‚îÉ Kode: \`${formattedCode}\`\n‚ï∞‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚ùç`,
        parse_mode: "Markdown",
        reply_markup: {
          inline_keyboard: [[{ text: "‚ùå Close", callback_data: "close" }]]
        }
      });
    } catch (e) {
      console.error("Gagal pairing:", e.message);
      await ctx.reply("‚ùå Gagal pairing. Pastikan nomor valid dan coba lagi.");
    }
  }
});

